public:
    int findContentChildren(vector<int>& g, vector<int>& s) {
        sort(g.begin(),g.end());
        sort(s.begin(),s.end());

        int child =0;
        int cookie=0;
        while(child<g.size()&&cookie<s.size()){
            if(s[cookie] >= g [child]){
                child++;
            }
            cookie++;
        }
        return child;
    }
};

