void ascendingSelectionSort(vector<int>&a,int n){
  int i,j,min_idx;
  for(i=0;i<n-1;i++){
    min_idx=i;
    for(j=i+1;j<n;j++){
      if(a[j]<a[min_idx])min_idx=j;
    }
    if(min_idx!=i)swap(a[min_idx],a[i]);
  }
  show(a);
}
void descendingSelectionSort(vector<int>&a,int n){
  int i,j,max_idx;
  for(i=0;i<n-1;i++){
    max_idx=i;
    for(j=i+1;j<n;j++){
      if(a[j]>a[max_idx])max_idx=j;
    }
    if(max_idx!=i)swap(a[max_idx],a[i]);
  }
  show(a);
}

