using namespace std;

// Stack using class
class Stack{
    int top;
    int capacity;
    int* arr;
public:
    Stack(int c){capacity=c;arr=new int[c];top=-1;}
    void push(int x){
        if(top==capacity-1){cout<<"Overflow";return;}
        arr[++top]=x;
    }
    void pop(){
        if(top==-1){cout<<"Underflow";return;}
        top--;
    }
    int peek(){return top==-1?-1:arr[top];}
    bool isEmpty(){return top==-1;}
};

int main(){
    Stack st(5);
    st.push(10);st.push(20);st.push(30);
    cout<<st.peek()<<endl;
    st.pop();cout<<st.peek();
    return 0;
}

