// BFS on adjacency list
void bfs(int start,unordered_map<int,vector<int>>&graph,int n){
    vector<bool>visited(n,false);
    queue<int>q;
    q.push(start);
    visited[start]=true;
    while(!q.empty()){
        int node=q.front();q.pop();
        cout<<node<<" ";
        for(auto neigh:graph[node]){
            if(!visited[neigh]){
                visited[neigh]=true;
                q.push(neigh);
            }
        }
    }
}

