// Dynamic programming Fibonacci
long long fibDP(int n){
    vector<long long>dp(n+1,0);
    dp[0]=0;dp[1]=1;
    for(int i=2;i<=n;i++){
        dp[i]=dp[i-1]+dp[i-2];
    }
    return dp[n];
}

