using namespace std;

// Queue using class
class Queue{
    int front,rear,capacity;
    int* arr;
public:
    Queue(int c){capacity=c;arr=new int[c];front=rear=0;}
    void enqueue(int x){
        if(rear==capacity){cout<<"Overflow";return;}
        arr[rear++]=x;
    }
    void dequeue(){
        if(front==rear){cout<<"Underflow";return;}
        front++;
    }
    int getFront(){return (front==rear)?-1:arr[front];}
    bool isEmpty(){return front==rear;}
};

int main(){
    Queue q(5);
    q.enqueue(1);q.enqueue(2);q.enqueue(3);
    cout<<q.getFront()<<endl;
    q.dequeue();cout<<q.getFront();
    return 0;
}

