using namespace std;

// Dijkstra's Algorithm for shortest path
struct Node{
    int v, w;
    bool operator>(const Node&other)const{
        return w>other.w;
    }
};

vector<int> dijkstra(int n,vector<vector<pair<int,int>>>&adj,int src){
    vector<int>dist(n,INT_MAX);
    dist[src]=0;
    priority_queue<Node,vector<Node>,greater<Node>>pq;
    pq.push({src,0});
    while(!pq.empty()){
        Node cur=pq.top();pq.pop();
        if(cur.w!=dist[cur.v]) continue;
        for(auto [nxt,wt]:adj[cur.v]){
            if(dist[cur.v]+wt<dist[nxt]){
                dist[nxt]=dist[cur.v]+wt;
                pq.push({nxt,dist[nxt]});
            }
        }
    }
    return dist;
}

int main(){
    int n,m;cin>>n>>m;
    vector<vector<pair<int,int>>>adj(n);
    for(int i=0;i<m;i++){
        int u,v,w;cin>>u>>v>>w;
        adj[u].push_back({v,w});
        adj[v].push_back({u,w});
    }
    vector<int>d=dijkstra(n,adj,0);
    for(int i=0;i<n;i++) cout<<d[i]<<" ";
    return 0;
}

explain code: #include<bits/stdc++.h>
using namespace std;

// Kruskal's Algorithm for Minimum Spanning Tree
struct Edge{
    int u,v,w;
    bool operator<(const Edge&other)const{return w<other.w;}
};

int findParent(int u,vector<int>&parent){
    if(parent[u]==u) return u;
    return parent[u]=findParent(parent[u],parent);
}

void unite(int u,int v,vector<int>&parent,vector<int>&rank){
    u=findParent(u,parent);
    v=findParent(v,parent);
    if(u!=v){
        if(rank[u]<rank[v]) swap(u,v);
        parent[v]=u;
        if(rank[u]==rank[v]) rank[u]++;
    }
}

int main(){
    int n,m;cin>>n>>m;
    vector<Edge>edges(m);
    for(int i=0;i<m;i++) cin>>edges[i].u>>edges[i].v>>edges[i].w;
    sort(edges.begin(),edges.end());
    vector<int>parent(n),rank(n,0);
    for(int i=0;i<n;i++) parent[i]=i;
    int mstWeight=0;
    for(auto &e:edges){
        if(findParent(e.u,parent)!=findParent(e.v,parent)){
            unite(e.u,e.v,parent,rank);
            mstWeight+=e.w;
        }
    }
    cout<<mstWeight;
    return 0;
}

